INSERT INTO product VALUES (1,'elegent red pump','http://www.lifestylelabels.com/steven-by-steve-madden-pryme-pump.html','http://www.lifestylelabels.com/media/catalog/product/cache/1/small_image/210x/9df78eab33525d08d6e5fb8d27136e95/s/t/steven-by-steve-madden-pryme-pump.jpg',299.0,0.0,1,'Nothing will turn his head faster than you wearing the sexy Pryme pump from Steven by Steve Madden. This daring pump has a pretty patent leather upper with light shirring, a double stitch detail surrounding the collar, and a vampy almond shaped toe.',1,1);
INSERT INTO product VALUES (2,'glittering red pump','http://www.lifestylelabels.com/nine-west-women-s-lucero-pump.html','http://www.lifestylelabels.com/media/catalog/product/cache/1/small_image/210x/9df78eab33525d08d6e5fb8d27136e95/n/i/nine-west-women-s-lucero-pump.jpg',289.99,0.0,1,'The Lucero pump from Nine West may just leave him at a loss for words. This flirty pump has a leather upper, a pretty almond-shaped toe with a slight V-cut vamp, leather linings, and a cushioned insole for long-wearing comfort.',1,1);
